uint16 OpcodeInternalEnumToRealValue(Opcodes opcd)
{
    if(_opcodeConversionMap.find(opcd) == _opcodeConversionMap.end())
        return 0x0;
    else
        return _opcodeConversionMap[opcd];
}

Opcodes OpcodeRealValueToInternalEnum(uint16 value)
{
    for(std::map<Opcodes, uint16>::const_iterator i = _opcodeConversionMap.begin() ; i != _opcodeConversionMap.end() ; ++i)
    {
        if(i->second == value)
            return i->first;
    }

    return NULL_OPCODE;
}
